import React, { Component } from 'react'

export default class Tab extends Component {
    constructor(props) {
        super(props);
    }

    _handleClick() {
        const { setCurrentTab, index } = this.props;
        setCurrentTab(index);
    }

    render() {
        const text = this.props;
        return(
            <button key={this.state.index} onClick={this._handleClick.bind(this)}>{this.state.text}</button>
        );
    }
}
