import React, { Component } from 'react';


export default class TabContainer extends Component {
    constructor(props) {
        super(props);

        this.state = {
            currentTab: this.props.defaultState,
        };

        this._data = this.props.data;
    }

    _setCurrentTab(currentTab) {
        if (this.state.currentTab === currentTab) {
            return;
        }
        this.setState({ currentTab: currentTab });
    }

    _renderTabs() {
        return this._data.map((tab, index) => {
            return (
                <div id="{index}">{tab.name}</div>
            );
        });
    }

    _renderTabPanel() {
        return this._data.map((tab, index) => {
            if (index === this.state.currentTab) {
                return(
                    <div>{tab.content}</div>
                );
            } else {
                return(<div>Should not be here</div>);
            }
        });
    }

    render() {
        const Tabs = this._renderTabs();
        const TabPanel = this._renderTabPanel();
        return (
            <div>
                <h1>Current Tab Index: {this.state.currentTab}</h1>
                {Tabs}
                {TabPanel}
            </div>
        );
    }
};
